SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_conta_pos_estab_contab_alt ON pls_conta_pos_estab_contab CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_conta_pos_estab_contab_alt() RETURNS trigger AS $BODY$
declare

ds_log_call_w			varchar(1500);
ds_observacao_w			varchar(4000);
nr_seq_prest_pgto_resumo_w	pls_conta_medica_resumo.nr_seq_prestador_pgto%type;

BEGIN

	if (coalesce(NEW.nr_seq_conta_resumo, -1) != coalesce(OLD.nr_seq_conta_resumo, -1)) then

		select 	max(nr_seq_prestador_pgto)
		into STRICT	nr_seq_prest_pgto_resumo_w
		from	pls_conta_medica_resumo
		where	nr_seq_conta = NEW.nr_seq_conta
		and	nr_sequencia = NEW.nr_seq_conta_resumo;

		if ( coalesce(NEW.nr_seq_prestador_pgto, -1) != coalesce(nr_seq_prest_pgto_resumo_w,-1)) then

			ds_log_call_w := substr(pls_obter_detalhe_exec(false),1,1500);

			ds_observacao_w := 'Conta: '||NEW.nr_seq_conta;
			ds_observacao_w :=	ds_observacao_w||chr(13)||chr(10)||
						'Conta resumo anterior: '||OLD.nr_seq_conta_resumo ||chr(13)||chr(10)||
						'Conta resumo novo: '||NEW.nr_seq_conta_resumo ||chr(13)||chr(10)||
						'Prestador pagto contab: '||NEW.nr_seq_prestador_pgto||' Prestador pagto resumo: '||nr_seq_prest_pgto_resumo_w||chr(13)||chr(10);

			insert into plsprco_cta(nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, nm_tabela,
						ds_log, ds_log_call, ds_funcao_ativa,
						ie_aplicacao_tasy, nm_maquina, ie_opcao,
						nr_seq_conta)
			values (nextval('plsprco_cta_seq'), LOCALTIMESTAMP, substr(coalesce(wheb_usuario_pck.get_nm_usuario,'Usuário não identificado '),1,14),
				LOCALTIMESTAMP, substr(coalesce(wheb_usuario_pck.get_nm_usuario,'Usuário não identificado '),1,14), 'PLS_CONTA_POS_ESTAB',
				ds_observacao_w, ds_log_call_w, obter_funcao_ativa,
				pls_se_aplicacao_tasy, wheb_usuario_pck.get_machine, 'POS',
				OLD.nr_seq_conta);
		end if;

	end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_conta_pos_estab_contab_alt() FROM PUBLIC;

CREATE TRIGGER pls_conta_pos_estab_contab_alt
	BEFORE UPDATE ON pls_conta_pos_estab_contab FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_conta_pos_estab_contab_alt();

