DROP TRIGGER IF EXISTS pls_contestacao_mat_update ON pls_contestacao_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_contestacao_mat_update() RETURNS trigger AS $BODY$
declare
nr_seq_conta_mat_w	pls_conta_mat.nr_sequencia%type;
nr_seq_analise_w	pls_conta.nr_seq_analise%type;

BEGIN
select	max(b.nr_sequencia),
	max(a.nr_seq_analise)
into STRICT	nr_seq_conta_mat_w,
	nr_seq_analise_w
from	pls_conta		a,
	pls_conta_mat		b
where	b.nr_seq_conta		= a.nr_sequencia
and	b.nr_sequencia		= OLD.nr_seq_conta_mat;

if	((NEW.vl_contestado > OLD.vl_material) and ((OLD.vl_contestado <> NEW.vl_contestado) or (OLD.vl_material <> NEW.vl_material))) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(170908, 'NR_SEQ_ANALISE_W=' || nr_seq_analise_w ||';'||
							'NR_SEQ_PROC_W=' || nr_seq_conta_mat_w);
end if;

if	((NEW.qt_contestada > OLD.qt_material) and ((OLD.qt_contestada <> NEW.qt_contestada) or (OLD.qt_material <> NEW.qt_material))) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(170909, 'NR_SEQ_ANALISE_W=' || nr_seq_analise_w ||';'||
							'NR_SEQ_PROC_W=' || nr_seq_conta_mat_w);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_contestacao_mat_update() FROM PUBLIC;

CREATE TRIGGER pls_contestacao_mat_update
	BEFORE INSERT OR UPDATE ON pls_contestacao_mat FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_contestacao_mat_update();

