SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_contrato_grupo_bef_insert ON pls_contrato_grupo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_contrato_grupo_bef_insert() RETURNS trigger AS $BODY$
declare
ie_tipo_relacionamento_w	pls_grupo_contrato.ie_tipo_relacionamento%type;
nr_seq_grupo_w			pls_contrato_grupo.nr_seq_grupo%type;
ie_permite_vinculo_w		varchar(1);

BEGIN
--Permitir vincular um contrato em mais de um grupo
ie_permite_vinculo_w	:= coalesce(obter_valor_param_usuario(1278, 7, Obter_Perfil_Ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 'N');

if (ie_permite_vinculo_w = 'N') then
	-- Tipo de relacionamento do grupo em que o contrato está.
	select	max(b.ie_tipo_relacionamento)
	into STRICT	ie_tipo_relacionamento_w
	from	pls_grupo_contrato b
	where	b.nr_sequencia = NEW.nr_seq_grupo;

	-- Verifica se o contrato está vinculado em outro grupo com o mesmo tipo de relacionamento
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_grupo_w
	from	pls_contrato_grupo a,
		pls_grupo_contrato b
	where	b.nr_sequencia = a.nr_seq_grupo
	and	a.nr_contrato = NEW.nr_contrato
	and	b.ie_tipo_relacionamento = ie_tipo_relacionamento_w;

	if (nr_seq_grupo_w is not null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(347963, 'NR_CONTRATO_W='   || NEW.nr_contrato ||
								';NR_SEQ_GRUPO='   || nr_seq_grupo_w);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_contrato_grupo_bef_insert() FROM PUBLIC;

CREATE TRIGGER pls_contrato_grupo_bef_insert
	BEFORE INSERT ON pls_contrato_grupo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_contrato_grupo_bef_insert();

