SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_contrato_pagador_update ON pls_contrato_pagador CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_contrato_pagador_update() RETURNS trigger AS $BODY$
declare

qt_registros_w	bigint;
qt_reg_w	smallint;
ds_email_w	pls_contrato_pagador.ds_email%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (TG_OP = 'UPDATE') then
	if	((NEW.cd_pessoa_fisica	<> OLD.cd_pessoa_fisica) or (NEW.cd_cgc		<> OLD.cd_cgc)) then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_segurado
		where	nr_seq_pagador	= NEW.nr_sequencia
		and	dt_liberacao is not null;

		if (qt_registros_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort( 267311, null ); --Não é possível alterar o pagador. O mesmo já está vinculado a um beneficiário!
		end if;
	end if;
end if;

if (NEW.ds_email is not null) then
	NEW.ds_email	:= replace(NEW.ds_email, ' ', '');

	while substr(NEW.ds_email,length(NEW.ds_email),1) = ';' loop
		BEGIN
		NEW.ds_email	:= substr(NEW.ds_email, 1, length(NEW.ds_email)-1);
		end;
	end loop;
end if;

<<Final>>
qt_reg_w	:= 0;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_contrato_pagador_update() FROM PUBLIC;

CREATE TRIGGER pls_contrato_pagador_update
	BEFORE INSERT OR UPDATE ON pls_contrato_pagador FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_contrato_pagador_update();

