SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_contrato_vendedor_update ON pls_contrato_vendedor CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_contrato_vendedor_update() RETURNS trigger AS $BODY$
declare

qt_registros_w		bigint;

BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_segurado
where	nr_seq_vendedor_canal	= NEW.nr_sequencia
and	dt_liberacao is not null;

if (qt_registros_w		> 0) and (NEW.nr_seq_vendedor	<> OLD.nr_seq_vendedor) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267007, null); /* Não é possível alterar o vendedor. O mesmo já está vinculado a um beneficiário! */
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_contrato_vendedor_update() FROM PUBLIC;

CREATE TRIGGER pls_contrato_vendedor_update
	BEFORE UPDATE ON pls_contrato_vendedor FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_contrato_vendedor_update();

