CREATE OR REPLACE FUNCTION trigger_fct_pls_contrato_web_bef_insupd() RETURNS trigger AS $BODY$
declare

qt_contrato_grupo_w	integer;
nr_seq_grupo_contrato_w	pls_grupo_contrato_web.nr_sequencia%type;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then
	if	((NEW.nr_seq_contrato is not null) or (NEW.nr_seq_intercambio is not null)) then
		select	max(nr_seq_grupo_contrato)
		into STRICT	nr_seq_grupo_contrato_w
		from	pls_grupo_contrato_web
		where	nr_sequencia = NEW.nr_seq_usuario_grupo;
		
		if (nr_seq_grupo_contrato_w is not null) then
			select	count(1)
			into STRICT	qt_contrato_grupo_w
			from (SELECT	nr_seq_grupo
				from	pls_contrato_grupo
				where	nr_seq_intercambio = NEW.nr_seq_intercambio
				
union

				SELECT	nr_seq_grupo
				from	pls_contrato_grupo
				where	nr_seq_contrato = NEW.nr_seq_contrato) alias2
			where	nr_seq_grupo = nr_seq_grupo_contrato_w;
				
			if (qt_contrato_grupo_w = 0) then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(1190620);
			end if;
		end if;
	end if;
end if;

RETURN NEW;
end
