DROP TRIGGER IF EXISTS pls_contr_regra_lanc_befinsup ON pls_contrato_regra_lanc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_contr_regra_lanc_befinsup() RETURNS trigger AS $BODY$
declare

qt_vendedor_canal_w	bigint;

BEGIN

if	(NEW.nr_seq_intercambio is not null AND NEW.nr_seq_vendedor_pf is not null) then
	if (NEW.nr_seq_vendedor_canal is not null) then
		select	count(1)
		into STRICT	qt_vendedor_canal_w
		from 	pls_vendedor_vinculado
		where 	nr_sequencia = NEW.nr_seq_vendedor_pf
		and 	nr_seq_vendedor = NEW.nr_seq_vendedor_canal;
	else
		qt_vendedor_canal_w	:= 0;
	end if;
	
	if (qt_vendedor_canal_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1123656);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
