CREATE TRIGGER pls_contr_regra_lanc_befinsup
	BEFORE INSERT OR UPDATE ON pls_contrato_regra_lanc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_contr_regra_lanc_befinsup();

