BEGIN

if	((NEW.cd_procedimento 		is not null) or (NEW.cd_area_procedimento 	is not null) or (NEW.cd_especialidade 		is not null) or (NEW.cd_grupo_proc 		is not null) or (NEW.ie_tipo_despesa 		is not null)) and (NEW.nr_seq_material 		is not null) then
