SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_cooperado_atual ON pls_cooperado CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_cooperado_atual() RETURNS trigger AS $BODY$
declare

dt_status_w		timestamp;

BEGIN
select	a.ie_status
into STRICT	NEW.ie_status
from	pls_situacao_cooperado	a
where	a.nr_sequencia	= NEW.nr_seq_situacao;

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

	select	CASE WHEN NEW.ie_status='A' THEN NEW.dt_inclusao WHEN NEW.ie_status='I' THEN NEW.dt_exclusao  ELSE null END
	into STRICT	dt_status_w
	;


	insert into pls_cooperado_situacao(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_cooperado,
		ds_motivo,
		dt_alteracao,
		dt_status,
		ie_situacao,
		nr_seq_motivo_susp,
		nr_seq_situacao_coop)
	values (nextval('pls_cooperado_situacao_seq'),
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		NEW.nr_sequencia,
		null,
		LOCALTIMESTAMP,
		dt_status_w,
		NEW.ie_status,
		null,
		NEW.nr_seq_situacao);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_cooperado_atual() FROM PUBLIC;

CREATE TRIGGER pls_cooperado_atual
	BEFORE UPDATE ON pls_cooperado FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_cooperado_atual();

