	dt_atualizacao_nrec, nm_usuario_nrec, vl_procedimento,
	vl_anestesista, vl_auxiliar, vl_custo_operacional,
	vl_filme, vl_medico
) values (
	nextval('pls_cp_cta_log_proc_hist_seq'), OLD.ds_log, OLD.dt_atualizacao,
	coalesce(OLD.ie_destino_regra,'P'), OLD.nm_usuario, OLD.nr_seq_conta_proc,
	LOCALTIMESTAMP, coalesce(wheb_usuario_pck.get_nm_usuario, 'naoidentificado'), OLD.vl_procedimento,
	OLD.vl_anestesista, OLD.vl_auxiliar, OLD.vl_custo_operacional,
	OLD.vl_filme, OLD.vl_medico
	);

RETURN OLD;
end
