DROP TRIGGER IF EXISTS pls_discussao_mat_insert ON pls_discussao_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_discussao_mat_insert() RETURNS trigger AS $BODY$
declare
 
vl_total_w		double precision;
qt_disc_mat_w		integer;
 
pragma autonomous_transaction;
 
BEGIN 
if (coalesce(wheb_usuario_pck.get_ie_lote_contabil,'N') = 'N') and (coalesce(wheb_usuario_pck.get_ie_atualizacao_contabil,'N') = 'N') then 
	if	((NEW.vl_aceito <> OLD.vl_aceito) or (NEW.vl_negado <> OLD.vl_negado)) and 
		((NEW.vl_aceito <> 0) or (NEW.vl_negado <> 0)) then 
		 
		vl_total_w := (coalesce(NEW.vl_aceito,0) + coalesce(NEW.vl_negado,0));
		 
		if (coalesce(OLD.vl_recurso,0) <> vl_total_w) and (coalesce(OLD.vl_contestado,0) <> vl_total_w) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(148398);
		end if;
	end if;
end if;
 
select	count(1) 
into STRICT	qt_disc_mat_w 
from	pls_discussao_mat 		d, 
	pls_contestacao_discussao	c, 
	pls_lote_discussao 		b, 
	pls_lote_contestacao		a 
where	b.nr_sequencia			= c.nr_seq_lote 
and	c.nr_sequencia			= d.nr_seq_discussao 
and	a.nr_sequencia			= b.nr_seq_lote_contest 
and	d.ie_tipo_acordo 		= '00' 
and	b.ie_tipo_arquivo 		= 1 
and	a.ie_envio_recebimento		= 'R' 
and	b.ie_status			<> 'C' -- Cancelado 
and	d.nr_seq_a500 			= NEW.nr_seq_a500 
and	d.nr_seq_conta_mat		= NEW.nr_seq_conta_mat 
and	d.vl_contestado 		<> NEW.vl_contestado 
and	d.nr_sequencia 			<> NEW.nr_sequencia 
and	d.vl_contestado is not null 
and	a.ie_status			<> 'C' -- Concluido 
and	a.nr_seq_pls_fatura is not null;
 
if (coalesce(qt_disc_mat_w, 0) > 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(307306);
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
