SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_evento_movimento_del ON pls_evento_movimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_evento_movimento_del() RETURNS trigger AS $BODY$
declare
ie_origem_w		varchar(1);
nr_seq_lote_pgto_w	bigint;
qt_rotina_w		bigint;


BEGIN
select	count(1)
into STRICT	qt_rotina_w
from 	v$session
where	audsid	= 	(SELECT userenv('sessionid')
			)
and	username = 	(select username
			from 	v$session
			where 	audsid = (	select userenv('sessionid')
						))
and (action like 'PLS_DESFAZER_LOTE_PAGAMENTO%'
or	action like 'PLS_CANCELAR_PAGTO_PRESTADOR%'
or	action like 'PLS_LIMPAR_PAGTO_ZERADOS_LOTE%');

if (qt_rotina_w = 0) and (OLD.nr_seq_lote_pgto is not null) then
	select	ie_origem,
		nr_seq_lote_pagamento
	into STRICT	ie_origem_w,
		nr_seq_lote_pgto_w
	from	pls_lote_evento a
	where	a.nr_sequencia = OLD.nr_seq_lote;

	if (ie_origem_w = 'A') and (nr_seq_lote_pgto_w is not null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(271814, 'NR_SEQ_LOTE_PGTO=' || OLD.nr_seq_lote_pgto);
	end if;
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_evento_movimento_del() FROM PUBLIC;

CREATE TRIGGER pls_evento_movimento_del
	BEFORE DELETE ON pls_evento_movimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_evento_movimento_del();

