DROP TRIGGER IF EXISTS pls_evento_movimento_update ON pls_evento_movimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_evento_movimento_update() RETURNS trigger AS $BODY$
declare

nr_seq_lote_pag_w	bigint;

BEGIN

if (NEW.vl_movto_original is null) then
	NEW.vl_movto_original	:= NEW.vl_movimento;
end if;

if (coalesce(wheb_usuario_pck.get_ie_lote_contabil,'N') = 'N') and (coalesce(wheb_usuario_pck.get_ie_atualizacao_contabil,'N') = 'N') then
	if (NEW.nr_seq_lote_pgto <> coalesce(OLD.nr_seq_lote_pgto,NEW.nr_seq_lote_pgto)) then
		select	max(a.nr_seq_lote_pagamento)
		into STRICT	nr_seq_lote_pag_w
		from	pls_lote_evento a
		where	a.nr_sequencia	= NEW.nr_seq_lote;

		if (NEW.nr_seq_lote_pgto <> nr_seq_lote_pag_w) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(215008);
		end if;
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
