SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_evento_regra_fixo_atual ON pls_evento_regra_fixo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_evento_regra_fixo_atual() RETURNS trigger AS $BODY$
declare

nr_seq_lote_w	pls_evento_movimento.nr_seq_lote%type;

BEGIN

if (coalesce(NEW.ie_forma_valor,'I') = 'I') and (NEW.vl_regra is null) then
	-- Este lançamento, deve possuir um valor informado.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266792);
end if;

select	coalesce(max(nr_seq_lote),0)
into STRICT	nr_seq_lote_w
from	pls_evento_movimento
where	nr_seq_regra_fixo	= NEW.nr_sequencia
and	ie_cancelamento is null;

if (nr_seq_lote_w > 0) and (NEW.ie_forma_incidencia = 'U') then
	--Não é possível alterar este lançamento!
	--Este lançamento único já está gerado no lote de ocorrências financeiras #@NR_SEQ_LOTE#@.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296438, 'NR_SEQ_LOTE=' || nr_seq_lote_w);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_evento_regra_fixo_atual() FROM PUBLIC;

CREATE TRIGGER pls_evento_regra_fixo_atual
	BEFORE INSERT OR UPDATE ON pls_evento_regra_fixo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_evento_regra_fixo_atual();

