SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_fatura_atual ON pls_fatura CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_fatura_atual() RETURNS trigger AS $BODY$
declare

BEGIN
if (coalesce(wheb_usuario_pck.get_ie_lote_contabil,'N') = 'N') and (coalesce(wheb_usuario_pck.get_ie_atualizacao_contabil,'N') = 'N') then
	if (NEW.dt_vencimento <> OLD.dt_vencimento) and (NEW.dt_vencimento is not null) then
		if (trunc(NEW.dt_vencimento) < trunc(LOCALTIMESTAMP)) then -- Menor que a data atual
			CALL wheb_mensagem_pck.exibir_mensagem_abort(260592);
		end if;

		if (trunc(NEW.dt_vencimento) > trunc(add_months(LOCALTIMESTAMP,120))) then -- Acima de 5 anos
			CALL wheb_mensagem_pck.exibir_mensagem_abort(260593);
		end if;
	end if;

	if (NEW.dt_vencimento_ndc <> OLD.dt_vencimento_ndc) and (NEW.dt_vencimento_ndc is not null) then
		if (trunc(NEW.dt_vencimento_ndc) < trunc(LOCALTIMESTAMP)) then -- Menor que a data atual
			CALL wheb_mensagem_pck.exibir_mensagem_abort(260592);
		end if;

		if (trunc(NEW.dt_vencimento_ndc) > trunc(add_months(LOCALTIMESTAMP,120))) then -- Acima de 5 anos
			CALL wheb_mensagem_pck.exibir_mensagem_abort(260593);
		end if;
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_fatura_atual() FROM PUBLIC;

CREATE TRIGGER pls_fatura_atual
	BEFORE INSERT OR UPDATE ON pls_fatura FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_fatura_atual();

