SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_fatura_mat_ins ON pls_fatura_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_fatura_mat_ins() RETURNS trigger AS $BODY$
declare
 
qt_registro_pos_w	bigint;
qt_registro_w		bigint;
 
BEGIN 
if (coalesce(wheb_usuario_pck.get_ie_lote_contabil,'N') = 'N') and (coalesce(wheb_usuario_pck.get_ie_atualizacao_contabil,'N') = 'N') then 
	select	count(1) 
	into STRICT	qt_registro_pos_w 
	from	pls_conta_pos_estabelecido a 
	where	nr_sequencia = NEW.nr_seq_conta_pos_estab 
	and	nr_seq_lote_fat is null 
	and	not exists (	SELECT	1 
				from	pls_conta_pos_estabelecido x 
				where	x.nr_seq_conta = a.nr_seq_conta 
				and	x.nr_sequencia <> a.nr_sequencia 
				and	nr_seq_lote_fat is not null);
 
	select	count(1) 
	into STRICT	qt_registro_w 
	from	pls_fatura_conta c, 
		pls_fatura_evento z, 
		pls_fatura s 
	where	s.nr_sequencia	= z.nr_seq_fatura 
	and	z.nr_sequencia	= c.nr_seq_fatura_evento 
	and	c.nr_sequencia	= NEW.nr_seq_fatura_conta 
	and	coalesce(s.ie_cancelamento,'X') in ('C','E');
 
	if (qt_registro_pos_w > 0) and (qt_registro_w = 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(264163,'NR_SEQ_CONTA_MAT=' || NEW.nr_seq_conta_mat);
	end if;
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_fatura_mat_ins() FROM PUBLIC;

CREATE TRIGGER pls_fatura_mat_ins
	BEFORE INSERT ON pls_fatura_mat FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_fatura_mat_ins();

