SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_grupo_plano_online_atual ON pls_grupo_plano_online CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_grupo_plano_online_atual() RETURNS trigger AS $BODY$
declare

qt_nr_seq_grupo_plano_w	bigint;

BEGIN

if (OLD.ie_tipo_operacao <> NEW.ie_tipo_operacao) then
	select	count(1)
	into STRICT	qt_nr_seq_grupo_plano_w
	from	pls_plano_online	a
	where	a.nr_seq_grupo_plano	= NEW.nr_sequencia;
	
	if (qt_nr_seq_grupo_plano_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1084160); --Não é possível realizar essa operação, pois há produtos vinculados a esse grupo
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_grupo_plano_online_atual() FROM PUBLIC;

CREATE TRIGGER pls_grupo_plano_online_atual
	BEFORE UPDATE ON pls_grupo_plano_online FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_grupo_plano_online_atual();

