SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_guia_plano_atual ON pls_guia_plano CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_guia_plano_atual() RETURNS trigger AS $BODY$
declare

vl_procedimentos_w	double precision;
vl_materiais_w		double precision;

BEGIN

if (NEW.nr_seq_segurado is not null) then

	select	coalesce(max(ie_tipo_segurado),'B')
	into STRICT	NEW.ie_tipo_segurado
	from	pls_segurado
	where	nr_sequencia	= NEW.nr_seq_segurado;

	/* Francisco - 23/05/2012 - Performance */

	if (coalesce(NEW.cd_guia, 'X') <> coalesce(OLD.cd_guia, 'X')) then
		NEW.cd_guia_pesquisa	:= pls_converte_cd_guia_pesquisa(NEW.cd_guia);
	end if;
end if;

-- se for guia de SP/SADT e prorrogação de internação verifica se tem uma guia principal
-- se tiver ela deve ser considerada
if (NEW.ie_tipo_guia in ('2', '8')) then
	if (NEW.nr_seq_guia_principal is not null) then
		NEW.nr_seq_guia_ok := NEW.nr_seq_guia_principal;
	end if;
end if;
-- se acima não atribuiu valor lança o valor da sequencia
if (NEW.nr_seq_guia_ok is null) then
	NEW.nr_seq_guia_ok := NEW.nr_sequencia;
end if;

select	coalesce(sum(vl_procedimento*qt_autorizada),0)
into STRICT	vl_procedimentos_w
from	pls_guia_plano_proc
where	nr_seq_guia	= NEW.nr_sequencia;

if (vl_procedimentos_w = 0) then
	select	coalesce(sum(vl_procedimento*qt_solicitada),0)
	into STRICT	vl_procedimentos_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= NEW.nr_sequencia;
end if;

select	coalesce(sum(vl_material*qt_autorizada),0)
into STRICT	vl_materiais_w
from	pls_guia_plano_mat
where	nr_seq_guia	= NEW.nr_sequencia;

if (vl_materiais_w = 0) then
	select	coalesce(sum(vl_material*qt_solicitada),0)
	into STRICT	vl_materiais_w
	from	pls_guia_plano_mat
	where	nr_seq_guia	= NEW.nr_sequencia;
end if;

NEW.vl_autorizacao	:= vl_procedimentos_w + vl_materiais_w;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_guia_plano_atual() FROM PUBLIC;

CREATE TRIGGER pls_guia_plano_atual
	BEFORE INSERT OR UPDATE ON pls_guia_plano FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_guia_plano_atual();

