DROP TRIGGER IF EXISTS pls_guia_plano_mat_atual ON pls_guia_plano_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_guia_plano_mat_atual() RETURNS trigger AS $BODY$
declare

cd_material_w	integer;
ie_erro_w	varchar(1)	:= 'N';
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (NEW.nr_seq_material is not null) then
		BEGIN
		select	cd_material
		into STRICT	cd_material_w
		from	pls_material a
		where	a.nr_sequencia	= NEW.nr_seq_material;
		exception
			when others then
			ie_erro_w	:= 'S';
		end;

		if (ie_erro_w = 'N') then
			NEW.cd_material	:= cd_material_w;
		end if;
	end if;
end if;

  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
