DROP TRIGGER IF EXISTS pls_inserir_atenc_saude_espec ON pls_prestador_med_espec CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_inserir_atenc_saude_espec() RETURNS trigger AS $BODY$
declare
C01 CURSOR(especialidade_p bigint) FOR
	SELECT	ie_tp_atencao_saude,
		dt_inicio_vigencia,
		dt_fim_vigencia
	from	pls_atencao_saude
	where	cd_especialidade = especialidade_p;


BEGIN
delete	FROM pls_prest_atencao_saude
where	nr_seq_prest_med_espec = OLD.nr_sequencia;

for	r_c01_w	in	C01( NEW.cd_especialidade ) loop
	insert into pls_prest_atencao_saude(	nr_sequencia,
						nr_seq_prest_med_espec,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						ie_tp_atencao_saude,
						dt_inicio_vigencia,
						dt_fim_vigencia)
	values (nextval('pls_prest_atencao_saude_seq'),
		NEW.nr_sequencia,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		r_c01_w.ie_tp_atencao_saude,
		coalesce(r_c01_w.dt_inicio_vigencia, LOCALTIMESTAMP),
		r_c01_w.dt_fim_vigencia);
end loop;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
