DROP TRIGGER IF EXISTS pls_itens_regra_partic_atual ON pls_itens_regra_partic CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_itens_regra_partic_atual() RETURNS trigger AS $BODY$
declare
nr_seq_regra_w		pls_oc_regra_participante.nr_sequencia%type;
nr_seq_grupo_regra_w	pls_oc_regra_participante.nr_sequencia%type;
BEGIN

-- se for apagar o registro
if (TG_OP = 'DELETE') then
	nr_seq_grupo_regra_w := OLD.nr_seq_grupo_regra;
else
	-- inserção ou atualização
	nr_seq_grupo_regra_w := NEW.nr_seq_grupo_regra;
end if;

-- se tiver grupo de regra
if (nr_seq_grupo_regra_w is not null) then

	-- busca a regra do grupo
	select	max(nr_seq_regra_partic)
	into STRICT	nr_seq_regra_w
	from	pls_oc_regra_grupo_partic
	where	nr_sequencia = nr_seq_grupo_regra_w;

	-- se tiver a regra sinaliza que é necessário atualizar a tabela
	if (nr_seq_regra_w is not null) then

		CALL pls_gerencia_upd_obj_pck.marcar_para_atualizacao(	'PLS_GRUPO_PARTIC_TM', wheb_usuario_pck.get_nm_usuario,
									'PLS_ITENS_REGRA_PARTIC_ATUAL',
									'nr_seq_regra_p=' || nr_seq_regra_w);
	end if;
end if;

IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_itens_regra_partic_atual() FROM PUBLIC;

CREATE TRIGGER pls_itens_regra_partic_atual
	BEFORE INSERT OR UPDATE OR DELETE ON pls_itens_regra_partic FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_itens_regra_partic_atual();

