SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_lancamento_mensalidade_bp ON pls_lancamento_mensalidade CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lancamento_mensalidade_bp() RETURNS trigger AS $BODY$
declare

qt_lancamento_mensalidade_w	bigint;

BEGIN
if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S')  then
	if (NEW.vl_lancamento = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(270595);
	elsif	(NEW.nr_seq_segurado is null AND NEW.nr_seq_pagador is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(270597);
	end if;

	if (TG_OP = 'INSERT') then
		NEW.vl_saldo := NEW.vl_lancamento;
	elsif (TG_OP = 'UPDATE') then
		select	count(1)
		into STRICT	qt_lancamento_mensalidade_w
		from	pls_mensalidade_seg_item a,
			pls_mensalidade_segurado b,
			pls_mensalidade c
		where	c.nr_sequencia = b.nr_seq_mensalidade
		and	b.nr_sequencia = a.nr_seq_mensalidade_seg
		and	c.ie_cancelamento is null
		and	a.nr_seq_lancamento_mens = NEW.nr_sequencia;
		
		if	(qt_lancamento_mensalidade_w > 0 AND OLD.ie_situacao = NEW.ie_situacao) then -- Ativar ou inativar o lançamento programado, mas não permite alterar suas caracteristicas caso esteja cobrado em mensalidade.
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1063980); --Lançamento programado já cobrado em mensalidade, não é possível modificar o lançamento.
		end if;

		if	(qt_lancamento_mensalidade_w = 0 AND NEW.dt_liberacao is null) then
			NEW.vl_saldo := NEW.vl_lancamento;
		end if;
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_lancamento_mensalidade_bp() FROM PUBLIC;

CREATE TRIGGER pls_lancamento_mensalidade_bp
	BEFORE INSERT OR UPDATE ON pls_lancamento_mensalidade FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_lancamento_mensalidade_bp();

