DROP TRIGGER IF EXISTS pls_log_pos_estab_insert ON pls_log_pos_estabelecido CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_log_pos_estab_insert() RETURNS trigger AS $BODY$
declare

ie_lote_gerado_w		varchar(1);
ie_gerando_lote_contab_w	varchar(1);
qt_registros_log_w		bigint;
dt_mes_competencia_w		timestamp;

BEGIN

select	max(a.dt_mes_competencia)
into STRICT	dt_mes_competencia_w
from	pls_protocolo_conta	a,
	pls_conta		b
where	a.nr_sequencia		= b.nr_seq_protocolo
and	b.nr_sequencia		= NEW.nr_seq_conta;

ie_lote_gerado_w		:= obter_se_lote_contabil_gerado(43,dt_mes_competencia_w);
ie_gerando_lote_contab_w	:= wheb_usuario_pck.get_ie_lote_contabil;

if (ie_lote_gerado_w = 'N') and (ie_gerando_lote_contab_w = 'N') then
	NEW.dt_atualizacao_nrec	:= dt_mes_competencia_w;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
