DROP TRIGGER IF EXISTS pls_lote_discussao_bef_insert ON pls_lote_discussao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_discussao_bef_insert() RETURNS trigger AS $BODY$
DECLARE

ie_status_w	pls_lote_contestacao.ie_status%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	/* Quando incluir uma nova discussão, mudar o status do lote de contestação caso esteja em aberto */

	select	a.ie_status
	into STRICT	ie_status_w
	from	pls_lote_contestacao a
	where	a.nr_sequencia	= NEW.nr_seq_lote_contest;

	if (ie_status_w not in ('E','D','C')) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(239436);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
