DROP TRIGGER IF EXISTS pls_lote_evento_del ON pls_lote_evento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_evento_del() RETURNS trigger AS $BODY$
declare
qt_rotina_w		bigint;


BEGIN
select	count(1)
into STRICT	qt_rotina_w
from 	v$session
where	audsid	= 	(SELECT userenv('sessionid')
			)
and	username = 	(select username
			from 	v$session
			where 	audsid = (	select userenv('sessionid')
						))
and	action like 'PLS_DESFAZER_LOTE_PAGAMENTO%';

if (qt_rotina_w = 0) and (OLD.nr_seq_lote_pagamento is not null) and (OLD.ie_origem = 'A') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(271816, 'NR_SEQ_LOTE_PGTO=' || OLD.nr_seq_lote_pagamento);
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_lote_evento_del() FROM PUBLIC;

CREATE TRIGGER pls_lote_evento_del
	BEFORE DELETE ON pls_lote_evento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_lote_evento_del();

