SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_lote_evento_update ON pls_lote_evento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_evento_update() RETURNS trigger AS $BODY$
declare

qt_eventos_divergentes_w 	bigint;

BEGIN
if (NEW.nr_seq_lote_pagamento <> OLD.nr_seq_lote_pagamento) then
	select	count(1)
	into STRICT	qt_eventos_divergentes_w
	from	pls_evento_movimento a
	where	a.nr_seq_lote 		= OLD.nr_sequencia
	and	a.nr_seq_lote_pgto 	<> NEW.nr_seq_lote_pagamento  LIMIT 1;

	if (qt_eventos_divergentes_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(215009);
	end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_lote_evento_update() FROM PUBLIC;

CREATE TRIGGER pls_lote_evento_update
	BEFORE UPDATE ON pls_lote_evento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_lote_evento_update();

