SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_lote_pagamento_update ON pls_lote_pagamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_pagamento_update() RETURNS trigger AS $BODY$
declare

ie_tributo_mes_w		varchar(10);

BEGIN

select	coalesce(max(ie_tributo_mes),'L')
into STRICT	ie_tributo_mes_w
from	pls_periodo_pagamento
where	nr_sequencia	= NEW.nr_seq_periodo;

if (ie_tributo_mes_w	= 'L') then
	NEW.dt_ref_tributo	:= NEW.dt_mes_competencia;
elsif (ie_tributo_mes_w	= 'A') then
	NEW.dt_ref_tributo	:= add_months(NEW.dt_mes_competencia,-1);
end if;

NEW.dt_mes_competencia	:= trunc(NEW.dt_mes_competencia, 'month');

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_lote_pagamento_update() FROM PUBLIC;

CREATE TRIGGER pls_lote_pagamento_update
	BEFORE INSERT OR UPDATE ON pls_lote_pagamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_lote_pagamento_update();

