CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_protocolo_update() RETURNS trigger AS $BODY$
declare

ie_mes_fechado_w		varchar(5);

BEGIN

select	pls_obter_se_mes_fechado(NEW.dt_mes_competencia,'T',NEW.cd_estabelecimento)
into STRICT	ie_mes_fechado_w
;

if (ie_mes_fechado_w	= 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 266996, null); /* O mês de competência ou contabilidade do mês está fechado. Verifique! */
end if;

RETURN NEW;
end
