SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_lote_prot_conta_alt ON pls_lote_protocolo_conta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_prot_conta_alt() RETURNS trigger AS $BODY$
declare
qt_rejeitado_w	integer;
ds_log_call_w	varchar(1500);
ds_log_w		varchar(4000);
nm_usuario_w	usuario.nm_usuario%type;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
	select	count(1)
	into STRICT	qt_rejeitado_w
	from	pls_protocolo_conta
	where	nr_seq_lote_conta = NEW.nr_sequencia
	and	ie_situacao	= 'RE';

	if (qt_rejeitado_w > 0) and (NEW.dt_confirmacao is not null) and (OLD.dt_confirmacao is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(273280);
	end if;

	if (OLD.ie_status = 'A' and coalesce(NEW.ie_status, 'Z') != 'A') then
		
		ds_log_call_w := substr(	''|| chr(13) ||chr(10)||
									' Funcao ativa : '|| obter_funcao_ativa || chr(13) ||chr(10)||
									' CallStack: '|| chr(13) || chr(10)|| dbms_utility.format_call_stack,1,1500);

		ds_log_w := ' Mudou status. Anterior: '||OLD.ie_status ||' novo: '||NEW.ie_status||''||ds_log_call_w;
		nm_usuario_w := substr(coalesce(wheb_usuario_pck.get_nm_usuario,'Nao identif.'),1,14);

		insert into pls_log_lote_prot_conta(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
						nm_usuario_nrec, ds_log, nr_seq_lote_protocolo)
			values (	nextval('pls_log_lote_prot_conta_seq'), LOCALTIMESTAMP, nm_usuario_w, LOCALTIMESTAMP,
						nm_usuario_w, ds_log_w , NEW.nr_sequencia);

	end if;
end if;


RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_lote_prot_conta_alt() FROM PUBLIC;

CREATE TRIGGER pls_lote_prot_conta_alt
	BEFORE UPDATE ON pls_lote_protocolo_conta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_lote_prot_conta_alt();

