CREATE OR REPLACE FUNCTION trigger_fct_pls_lote_reaj_segurado_update() RETURNS trigger AS $BODY$
declare

qt_registros_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_segurado_preco
where	nr_seq_lote	= NEW.nr_sequencia;

if (qt_registros_w		> 0) and (OLD.dt_mesano_referencia <> NEW.dt_mesano_referencia) then
	-- Não é possível alterar a data de referência deste lote pois já existem preços calculados para o mesmo!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266946);
end if;

RETURN NEW;
end
