DROP TRIGGER IF EXISTS pls_material_delete ON pls_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_material_delete() RETURNS trigger AS $BODY$
declare
ie_possui_reg_w		integer;

BEGIN

select	count(*)
into STRICT	ie_possui_reg_w
from	material_tipo_local_est
where	cd_material = OLD.cd_material;

if (ie_possui_reg_w > 0) then
	update	material_tipo_local_est
	set	ie_operadora	= 'X'
	where	cd_material	= OLD.cd_material;
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_material_delete() FROM PUBLIC;

CREATE TRIGGER pls_material_delete
	BEFORE DELETE ON pls_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_material_delete();

