SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_material_update ON pls_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_material_update() RETURNS trigger AS $BODY$
declare
qt_registro_w		integer;
dt_referencia_w		timestamp := trunc(NEW.dt_atualizacao);
cd_empresa_w		estabelecimento.cd_empresa%type := obter_empresa_estab(OLD.cd_estabelecimento);

BEGIN
select	count(1)
into STRICT	qt_registro_w
from	regra_resp_revisao_mat
where	cd_empresa	= cd_empresa_w
and	dt_referencia_w between dt_inicio_vigencia and coalesce(fim_dia(dt_fim_vigencia),dt_referencia_w);

if (qt_registro_w > 0) then
	select	count(1)
	into STRICT	qt_registro_w
	from	material_tipo_local_est
	where	cd_material = OLD.cd_material;

	if (qt_registro_w > 0) and (OLD.ie_revisar = NEW.ie_revisar) then
		NEW.ie_revisar	:= 'R';
		gerar_comunic_revisao_mat(NEW.cd_material, NEW.nr_sequencia, dt_referencia_w,OLD.cd_estabelecimento, NEW.nm_usuario);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_material_update() FROM PUBLIC;

CREATE TRIGGER pls_material_update
	BEFORE UPDATE ON pls_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_material_update();

