declare
 
BEGIN 
-- Caso tenha alguma alteração no valor apresentado ou pago, zera os valores totais do lote 
if (NEW.vl_cobranca_guia <> OLD.vl_cobranca_guia) or (NEW.vl_total_pago <> OLD.vl_total_pago) then 
	CALL pls_gerencia_envio_ans_pck.atualiza_valores_totais_lote(NEW.nr_seq_lote_monitor, NEW.nm_usuario, 'Z');
end if;
 
RETURN NEW;
end
