DROP TRIGGER IF EXISTS pls_notific_regra_excecao_att ON pls_notific_regra_excecao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_notific_regra_excecao_att() RETURNS trigger AS $BODY$
declare

nr_seq_contrato_w	pls_contrato.nr_sequencia%type;

BEGIN

if (NEW.nr_contrato is not null) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato = NEW.nr_contrato;

	if (nr_seq_contrato_w is not null) then
		NEW.nr_seq_contrato := nr_seq_contrato_w;
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(853575); --O contrato informado n√£o existe. Verifique!
	end if;
else
	NEW.nr_seq_contrato	:= null;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
