CREATE TRIGGER pls_notific_regra_excecao_att
	BEFORE INSERT OR UPDATE ON pls_notific_regra_excecao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_notific_regra_excecao_att();

