CREATE OR REPLACE FUNCTION trigger_fct_pls_ocorrencia_atual() RETURNS trigger AS $BODY$
declare
ie_analise_cm_nova_w	pls_parametros.ie_analise_cm_nova%type;
BEGIN
--aloterado o tratamento pois clientes que não fazem uso da análise porém querem utilizar o processo de ocorrência não conseguiam fazer uso do mesmo corretamente dgkorz
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then

	select	ie_analise_cm_nova
	into STRICT	ie_analise_cm_nova_w
	from	table(pls_parametros_pck.f_retorna_param(NEW.cd_estabelecimento));

	if (NEW.ie_auditoria_conta = 'N') and (NEW.nr_seq_motivo_glosa is not null) and (ie_analise_cm_nova_w = 'S')then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(210985);
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
