SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_ocorrencia_grupo_atual ON pls_ocorrencia_grupo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_ocorrencia_grupo_atual() RETURNS trigger AS $BODY$
declare
ie_alerta_w			varchar(255)	:= 'N';
nr_seq_fluxo_w			bigint;

BEGIN
if (coalesce(NEW.ie_autorizacao,'S') = 'S') and (NEW.nr_seq_fluxo is null) then
	ie_alerta_w	:= 'S';
end if;

if (coalesce(NEW.ie_conta_medica,'S') = 'S') then
	select	max(a.nr_seq_fluxo_padrao)
	into STRICT	nr_seq_fluxo_w
	from	pls_grupo_auditor	a
	where	a.nr_sequencia	= NEW.nr_seq_grupo;

	if (nr_seq_fluxo_w is null) and (NEW.nr_seq_fluxo is null) then
		ie_alerta_w	:= 'S';
	end if;
end if;

if (ie_alerta_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(214142);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_ocorrencia_grupo_atual() FROM PUBLIC;

CREATE TRIGGER pls_ocorrencia_grupo_atual
	BEFORE INSERT OR UPDATE ON pls_ocorrencia_grupo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_ocorrencia_grupo_atual();

