SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_ocorrencia_regra_atual ON pls_ocorrencia_regra CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_ocorrencia_regra_atual() RETURNS trigger AS $BODY$
declare

cd_material_w	integer;
qt_regra_comp_w	bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (NEW.nr_seq_material is not null) then
		select	cd_material
		into STRICT	cd_material_w
		from	pls_material a
		where	a.nr_sequencia	= NEW.nr_seq_material;

		NEW.cd_material	:= cd_material_w;
	end if;

	if (NEW.nr_seq_prestador is null and OLD.nr_seq_prestador is not null) then
		select	count(1)
		into STRICT	qt_regra_comp_w
		from	PLS_OCOR_REGRA_PREST
		where	nr_seq_ocor_regra = NEW.nr_sequencia  LIMIT 1;

		if (qt_regra_comp_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(233686);
		end if;
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_ocorrencia_regra_atual() FROM PUBLIC;

CREATE TRIGGER pls_ocorrencia_regra_atual
	BEFORE INSERT OR UPDATE ON pls_ocorrencia_regra FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_ocorrencia_regra_atual();

