CREATE OR REPLACE FUNCTION trigger_fct_pls_oc_regra_partic_atual() RETURNS trigger AS $BODY$
declare
nr_seq_regra_w	pls_oc_regra_participante.nr_sequencia%type;
BEGIN

-- se for apagar o registro
if (TG_OP = 'DELETE') then
	nr_seq_regra_w := OLD.nr_sequencia;

-- se for atualização
elsif (TG_OP = 'UPDATE') then
	-- só atualiza os registros da tabela TM se mudar a situação
	-- não faz sentido atualizar se mudou apenas a descrição da regra
	if (coalesce(NEW.ie_situacao, 'A') != coalesce(OLD.ie_situacao, 'A')) then
		nr_seq_regra_w := NEW.nr_sequencia;
	else
		nr_seq_regra_w := null;
	end if;
else
	-- inserção
	nr_seq_regra_w := NEW.nr_sequencia;
end if;

-- se tiver a regra sinaliza que é necessário atualizar a tabela
if (nr_seq_regra_w is not null) then
	CALL pls_gerencia_upd_obj_pck.marcar_para_atualizacao(	'PLS_GRUPO_PARTIC_TM', wheb_usuario_pck.get_nm_usuario,
								'PLS_OC_REGRA_PARTIC_ATUAL',
								'nr_seq_regra_p=' || nr_seq_regra_w);
end if;

IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end
