CREATE OR REPLACE FUNCTION trigger_fct_pls_pj_empresa_insert_update() RETURNS trigger AS $BODY$
declare

qt_registro_w		bigint;

pragma autonomous_transaction;
BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_pj_empresa
where	cd_empresa = NEW.cd_empresa
and	nr_sequencia <> NEW.nr_sequencia;

if (qt_registro_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 195585, null);
	/* Já existe uma empresa cadastrada com o código informado. Favor verifique. */

end if;

RETURN NEW;
end
