SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_plano_online_atual ON pls_plano_online CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_plano_online_atual() RETURNS trigger AS $BODY$
declare

ie_tipo_operacao_grupo_w	pls_grupo_plano_online.ie_tipo_operacao%type;
ie_tipo_operacao_plano_w	pls_plano.ie_tipo_operacao%type;
qt_registro_w	integer;
qt_nr_seq_grupo_plano_w		smallint;

BEGIN
select	ie_tipo_operacao
into STRICT	ie_tipo_operacao_plano_w
from	pls_plano
where	nr_sequencia	= NEW.nr_seq_plano;

select	ie_tipo_operacao
into STRICT	ie_tipo_operacao_grupo_w
from	pls_grupo_plano_online
where	nr_sequencia	= NEW.nr_seq_grupo_plano;

if (TG_OP = 'INSERT') and (ie_tipo_operacao_grupo_w = 'A') then
	select	count(1)
	into STRICT	qt_nr_seq_grupo_plano_w
	from	pls_plano_online
	where	nr_seq_grupo_plano = NEW.nr_seq_grupo_plano;
	
	if (qt_nr_seq_grupo_plano_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1084171);
	end if;
end if;

if (ie_tipo_operacao_grupo_w is not null) and (ie_tipo_operacao_plano_w is not null) and (ie_tipo_operacao_grupo_w <> ie_tipo_operacao_plano_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1084154);
end if;

if (TG_OP = 'UPDATE') and (NEW.nr_seq_plano <> OLD.nr_seq_plano) then
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_plano_tabela_online
	where	nr_seq_plano_online	= NEW.nr_sequencia;
	
	if (qt_registro_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1093684); --Não é possível alterar o produto, pois já existe tabela de preço vinculada.
	end if;
end if;

select	count(*)
into STRICT	qt_registro_w
from	pls_plano
where	nr_sequencia	= NEW.nr_seq_plano
and	ie_tipo_contratacao = 'I';

if (qt_registro_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1093685); --Somente é permitida a inclusão de produtos com tipo de contratação Individual/Familiar
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_plano_online_atual() FROM PUBLIC;

CREATE TRIGGER pls_plano_online_atual
	BEFORE INSERT OR UPDATE ON pls_plano_online FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_plano_online_atual();

