DROP TRIGGER IF EXISTS pls_plano_tabela_online_atual ON pls_plano_tabela_online CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_plano_tabela_online_atual() RETURNS trigger AS $BODY$
declare

nr_seq_plano_regra_w	pls_plano.nr_sequencia%type;
nr_seq_plano_tabela_w	pls_plano.nr_sequencia%type;
nr_contrato_w		pls_tabela_preco.nr_contrato%type;
nr_segurado_w		pls_tabela_preco.nr_segurado%type;

BEGIN

select	max(nr_seq_plano)
into STRICT	nr_seq_plano_regra_w
from	pls_plano_online
where	nr_sequencia	= NEW.nr_seq_plano_online;

select	max(nr_seq_plano),
	max(nr_contrato),
	max(nr_segurado)
into STRICT	nr_seq_plano_tabela_w,
	nr_contrato_w,
	nr_segurado_w
from	pls_tabela_preco
where	nr_sequencia	= NEW.nr_seq_tabela;

if (nr_seq_plano_regra_w <> nr_seq_plano_tabela_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1093636);
end if;

if (nr_contrato_w is not null) or (nr_segurado_w is not null) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1093678);
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
