SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_prestador_atual ON pls_prestador CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_prestador_atual() RETURNS trigger AS $BODY$
declare
nm_medico_ou_prestador_w	pls_prestador.nm_busca_guia_medico%type;
nm_prestador_w			varchar(255);
nm_usual_w			varchar(255);

BEGIN

-- se for insert ou modificou o valor do campo cd_prestador atualiza os campos de redund√¢ncia formatados
if	((TG_OP = 'INSERT') or (coalesce(NEW.cd_prestador, 'A') != coalesce(OLD.cd_prestador, 'A'))) then

	-- converte para otimizar as buscas
	NEW.cd_prestador_upper := upper(NEW.cd_prestador);
	NEW.cd_prestador_upper_espe := elimina_caractere_especial(upper(NEW.cd_prestador));
end if;

if	((NEW.cd_cgc <> OLD.cd_cgc) or (NEW.cd_pessoa_fisica <> OLD.cd_pessoa_fisica) or (coalesce(NEW.ie_prefixo_guia_medico,'X') <> coalesce(OLD.ie_prefixo_guia_medico,'X')) or (coalesce(NEW.nm_adic_guia_medico,'X') <> coalesce(OLD.nm_adic_guia_medico,'X')) or (coalesce(NEW.nm_interno,'X') <> coalesce(OLD.nm_interno,'X'))) then
	if (NEW.cd_cgc is not null) then
		select	a.ds_razao_social,
			a.nm_fantasia
		into STRICT	nm_prestador_w,
			nm_usual_w
		from	pessoa_juridica a
		where	a.cd_cgc	= NEW.cd_cgc;
	elsif (NEW.cd_pessoa_fisica is not null) then
		select	a.nm_pessoa_fisica,
			b.nm_guerra
		into STRICT	nm_prestador_w,
			nm_usual_w
		FROM pessoa_fisica a
LEFT OUTER JOIN medico b ON (a.cd_pessoa_fisica = b.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica	= NEW.cd_pessoa_fisica;
	end if;

	nm_medico_ou_prestador_w	:= pls_obter_nome_busca_guia(NEW.ie_prefixo_guia_medico,
								     nm_prestador_w,
								     nm_usual_w,
								     NEW.nm_interno,
								     NEW.nm_adic_guia_medico);

	NEW.nm_busca_guia_medico	:= nm_medico_ou_prestador_w;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_prestador_atual() FROM PUBLIC;

CREATE TRIGGER pls_prestador_atual
	BEFORE INSERT OR UPDATE ON pls_prestador FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_prestador_atual();

