DROP TRIGGER IF EXISTS pls_prestador_medico_atual ON pls_prestador_medico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_prestador_medico_atual() RETURNS trigger AS $BODY$
declare

nm_medico_w			pessoa_fisica.nm_pessoa_fisica%type;
nm_guerra_w			medico.nm_guerra%type;

BEGIN
if (NEW.cd_medico is not null) and
	((OLD.nm_busca_guia_medico is null AND NEW.nm_busca_guia_medico is null) or (NEW.cd_medico <> OLD.cd_medico)) then
	select	max(a.nm_pessoa_fisica),
		max(b.nm_guerra)
	into STRICT	nm_medico_w,
		nm_guerra_w
	FROM pessoa_fisica a
LEFT OUTER JOIN medico b ON (a.cd_pessoa_fisica = b.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica	= NEW.cd_medico;

	NEW.nm_busca_guia_medico := substr(pls_obter_nome_busca_guia(	null, nm_medico_w, nm_guerra_w, null, null),1,2000);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_prestador_medico_atual() FROM PUBLIC;

CREATE TRIGGER pls_prestador_medico_atual
	BEFORE INSERT OR UPDATE ON pls_prestador_medico FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_prestador_medico_atual();

