SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_prestador_taxa_adm_atual ON pls_prestador_taxa_adm CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_prestador_taxa_adm_atual() RETURNS trigger AS $BODY$
declare

qt_material_w		integer;

BEGIN
--OBSERVAÇÃO:
--Retirado a tratativa de preenchimento do campo cd_material
-- e passsado pra realizar esse processo no atrib change do nr_sequencia no delphi
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (NEW.nr_seq_material is not null) then
		select	count(1)
		into STRICT	qt_material_w
		from	pls_material a
		where	a.nr_sequencia	= NEW.nr_seq_material;

		if (qt_material_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(451885);
		end if;
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_prestador_taxa_adm_atual() FROM PUBLIC;

CREATE TRIGGER pls_prestador_taxa_adm_atual
	BEFORE INSERT OR UPDATE ON pls_prestador_taxa_adm FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_prestador_taxa_adm_atual();

