SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_prior_analise_aut_update ON pls_prioridade_analise_aut CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_prior_analise_aut_update() RETURNS trigger AS $BODY$
declare
qt_analise_w			bigint	:= 0;

BEGIN
if (NEW.ie_prioridade <> OLD.ie_prioridade) then
	select	count(1)
	into STRICT	qt_analise_w
	from	pls_auditoria	a
	where	a.nr_seq_prioridade_analise	= NEW.nr_sequencia;

	if (qt_analise_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(253365);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_prior_analise_aut_update() FROM PUBLIC;

CREATE TRIGGER pls_prior_analise_aut_update
	BEFORE UPDATE ON pls_prioridade_analise_aut FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_prior_analise_aut_update();

