CREATE OR REPLACE FUNCTION trigger_fct_pls_proposta_adesao_atual() RETURNS trigger AS $BODY$
DECLARE
ie_data_reatroativa_w	varchar(1);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'N')  then
	goto Final;
end if;

ie_data_reatroativa_w	:= coalesce(obter_valor_param_usuario(1232, 110, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo),'S');

if (ie_data_reatroativa_w = 'N') then
	if	((TG_OP = 'INSERT') or (NEW.dt_inicio_proposta <> OLD.dt_inicio_proposta)) and (trunc(NEW.dt_inicio_proposta,'dd') < trunc(LOCALTIMESTAMP,'dd')) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(361862); --Não é possível cadastrar proposta com data retroativa. Parâmetro [110]
	end if;
end if;

<<Final>>
ie_data_reatroativa_w	:= 'S';
RETURN NEW;
end
