DROP TRIGGER IF EXISTS pls_reaj_cobr_insert_update ON pls_reajuste_cobr_retro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_reaj_cobr_insert_update() RETURNS trigger AS $BODY$
declare
dt_reajuste_w            pls_reajuste.dt_reajuste%type;
BEGIN

select	trunc(a.dt_reajuste,'month')
into STRICT   	dt_reajuste_w
from 	pls_reajuste  a
where   a.nr_sequencia = NEW.nr_seq_reajuste;
 
if (trunc(NEW.dt_referencia_reajuste,'month') < dt_reajuste_w) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1146431);
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
