DROP TRIGGER IF EXISTS pls_regra_preco_proc_befins ON pls_regra_preco_proc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_regra_preco_proc_befins() RETURNS trigger AS $BODY$
declare
ie_classificacao_w	varchar(1);
ie_verifica_proc_w	varchar(1);

BEGIN
/* Obter o valor do parâmetro 7 da função OPS - Regras e Critérios de Preço - 1298 */

ie_verifica_proc_w	:= coalesce(obter_valor_param_usuario(1298, 7, Obter_Perfil_Ativo, NEW.nm_usuario, NEW.cd_estabelecimento), 'N');

if (ie_verifica_proc_w	= 'S') then
	select	coalesce(max(ie_classificacao),1)
	into STRICT	ie_classificacao_w
	from	procedimento
	where	NEW.cd_procedimento = cd_procedimento
	and	ie_origem_proced = NEW.ie_origem_proced;

	if (ie_classificacao_w <> '1') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267215, null ); /* Não é permitido cadastrar diárias ou serviços na pasta Procedimentos! [7] */
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
